mapper.select:


select * from t_shangpin

WHERE 1=1



	 AND shangpinDate >= #{sdate,jdbcType=TIMESTAMP} 





	 AND shangpinDate <= #{edate,jdbcType=TIMESTAMP} 







		 AND shangpinName LIKE  '%${shangpin.shangpinName}%' 





		 AND shangpinId = #{shangpin.shangpinId} 





		 AND sptypeId = #{shangpin.sptypeId} 





		 AND shangpinType = #{shangpin.shangpinType} 





		 AND shangpinZong > #{shangpin.shangpinType1} 





		 AND userId = #{shangpin.userId} 





		 AND bumenId = #{shangpin.bumenId} 





		 AND roleId = #{shangpin.roleId} 




order by shangpinId 



	limit #{page},#{rows}
