mapper.select:

select yhbumenId, yhbumenName, yhbumenMark, yhbumenMark1, yhbumenMark2
from t_yhbumen
where yhbumenId = #{yhbumenId,jdbcType=INTEGER}



select * from t_yhbumen

WHERE 1=1





		AND yhbumenName LIKE '%${yhbumen.yhbumenName}%'





		AND yhbumenId = #{yhbumen.yhbumenId}





		AND yhbumenMark2 = #{yhbumen.yhbumenMark2}




order by yhbumenId



	limit #{page},#{rows}
