mapper.select:

select * from t_yonghu
WHERE 1=1

	 AND yonghuName = #{yonghuName,jdbcType=VARCHAR} 


	 AND yonghuDate1 >= #{sdate,jdbcType=TIMESTAMP} 


	 AND yonghuDate1 <= #{edate,jdbcType=TIMESTAMP} 



		 AND yonghuName LIKE  '%${yonghu.yonghuName}%' 


		 AND yonghuPassword = #{yonghu.yonghuPassword} 


		 AND yonghuId = #{yonghu.yonghuId} 


		 AND yonghuType1 = #{yonghu.yonghuType1} 


		 AND yonghuType2 = #{yonghu.yonghuType2} 


		 AND yhroleId = #{yonghu.yhroleId} 


		 AND yonghuSex = #{yonghu.yonghuSex} 


		 AND yhbumenId = #{yonghu.yhbumenId} 


order by yonghuId 

	limit #{page},#{rows}
