mapper.select:

select * from t_yxinxi
WHERE 1=1

	 AND yxinxiDate >= #{sdate,jdbcType=TIMESTAMP} 


	 AND yxinxiDate <= #{edate,jdbcType=TIMESTAMP} 



		 AND yxinxiName LIKE  '%${yxinxi.yxinxiName}%' 


		 AND yxinxiId = #{yxinxi.yxinxiId} 


		 AND yxtypeId = #{yxinxi.yxtypeId} 


		 AND yxinxiType = #{yxinxi.yxinxiType} 


		 AND yxinxiType1 = #{yxinxi.yxinxiType1} 


		 AND yonghuId = #{yxinxi.yonghuId} 


		 AND yhbumenId = #{yxinxi.yhbumenId} 


order by yxinxiId 

	limit #{page},#{rows}
