mapper.select:


select * from t_spgys

WHERE 1=1



	 AND spgysDate >= #{sdate,jdbcType=TIMESTAMP} 





	 AND spgysDate <= #{edate,jdbcType=TIMESTAMP} 







		AND spgysName LIKE '%${spgys.spgysName}%'





		AND spgysId = #{spgys.spgysId}





		 AND spgysType = #{uxinxi.spgysType} 





		 AND spgysType1 = #{uxinxi.spgysType1} 




order by spgysId



	limit #{page},#{rows}
